const platformColors = {
  spotify: '#1DB954',
  youtube: '#FF0000',
  instagram: 'linear-gradient(45deg, #f09433 0%,#e6683c 25%,#dc2743 50%,#cc2366 75%,#bc1888 100%)',
  tiktok: '#000000',
  soundcloud: '#FF5500',
};

/**
 * StatCounter - Animated number display with optional platform icon
 * Features smooth counting animation, glow effect, and platform branding
 */
export function StatCounter({
  value,
  label,
  prefix = '',
  suffix = '',
  platform,
  duration = 2,
  className,
}: StatCounterProps) {
  const count = useMotionValue(0);
  const rounded = useTransform(count, (latest) => {
    if (latest >= 1_000_000_000) {
      return `${(latest / 1_000_000_000).toFixed(1)}B`;
    }
    if (latest >= 1_000_000) {
      return `${(latest / 1_000_000).toFixed(1)}M`;
    }
    if (latest >= 1_000) {
      return `${(latest / 1_000).toFixed(1)}K`;
    }
    return Math.round(latest).toString();
  });

  const [displayValue, setDisplayValue] = useState('0');
  const [hasAnimated, setHasAnimated] = useState(false);
  const ref = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const unsubscribe = rounded.on('change', (latest) => {
      setDisplayValue(latest);
    });
    return () => unsubscribe();
  }, [rounded]);

  useEffect(() => {
    if (hasAnimated) return;

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting && !hasAnimated) {
            setHasAnimated(true);
            animate(count, value, { duration });
          }
        });
      },
      { threshold: 0.5 }
    );

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => observer.disconnect();
  }, [count, value, duration, hasAnimated]);

  return (
    <motion.div
      ref={ref}
      className={cn(
        'flex flex-col items-center text-center',
        'p-6 md:p-8',
        className
      )}
      initial={{ opacity: 0, y: 20 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.5 }}
    >
      {/* Platform Icon */}
      {platform && (
        <div
          className="mb-4"
          style={{
            color: platform === 'instagram' ? '#E1306C' : platformColors[platform],
          }}
        >
          {platformIcons[platform]}
        </div>
      )}

      {/* Value */}
      <div
        className={cn(
          'font-display text-4xl md:text-5xl lg:text-6xl',
          'text-white uppercase tracking-tight',
          'mb-2'
        )}
        style={{
          textShadow: platform
            ? `0 0 30px ${platform === 'instagram' ? '#E1306C' : platformColors[platform]}50`
            : '0 0 30px rgba(255, 0, 64, 0.5)',
        }}
      >
        {prefix}
        {displayValue}
        {suffix}
      </div>

      {/* Label */}
      <p
        className={cn(
          'font-body text-sm md:text-base',
          'text-text-secondary uppercase tracking-widest'
        )}
      >
        {label}
      </p>
    </motion.div>
  );
}

// Grid wrapper for multiple stats
export function StatGrid({
  children,
  className,
}: {
  children: React.ReactNode;
  className?: string;
}) {
  return (
    <div
      className={cn(
        'grid grid-cols-2 md:grid-cols-4 gap-4 md:gap-8',
        'border-t border-b border-white/10',
        'py-8 md:py-12',
        className
      )}
    >
      {children}
    </div>
  );
}

export default StatCounter;
